    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

//        if(inputAmount.getText().equals("")||inputType.getText().equals(""))
        if (inputAmount.getText().equals("")) {
            JOptionPane.showMessageDialog(this, "Please enter all data.");

        } else {
            String data[] = {inputAmount.getText(), inputType.getSelectedItem().toString()};

            DefaultTableModel tblModel = (DefaultTableModel) jTable1.getModel();
            tblModel.addRow(data);
            JOptionPane.showMessageDialog(this, "Saved");

            inputAmount.setText("");

            if (inputType.getSelectedItem().equals("Received")) {
                int numrow = jTable1.getRowCount();
                double tot = 0;

                for (int i = 0; i < numrow; i++) {
                    double val = Double.valueOf(jTable1.getValueAt(i, 0).toString());
                    tot += val;
            
                }

                printIncome.setText(Double.toString(tot));
            } else {
                int numroww = jTable1.getRowCount();
                double tott = 0;

                for (int i = 0; i < numroww; i++) {
                    double val = Double.valueOf(jTable1.getValueAt(i, 0).toString());
                    tott -= val;
                }

                printWithdraw.setText(Double.toString(tott));
            }

        }
    }//GEN-LAST:event_jButton1ActionPerformed

